        DB::table("categories")->insert(["id" => 1, "name" => "daily_dozen", "created_at" => "2020-10-14 15:06:59", "updated_at" => "2020-10-14 15:06:59"]);
        DB::table("categories")->insert(["id" => 2, "name" => "supplements", "created_at" => "2020-10-14 15:06:59", "updated_at" => "2020-10-14 15:06:59"]);
